INSERT INTO `cms`.`t_product` (`id`, `brief`, `content`, `createTime`, `thumbnail`, `title`, `typeId`, `typeName`, `updateTime`, `hot`, `recommend`) VALUES ('100001', '580元', '<p>&nbsp;</p><p><img src=\"/cms/resources/ueditor/jsp//upload/20150813/41591439427029455.jpg\"/></p><p>品&nbsp;&nbsp;&nbsp; 牌:美国tasco&nbsp; <br/>型&nbsp;&nbsp;&nbsp; 号：wp1025 <br/>倍&nbsp;&nbsp;&nbsp; 率：10倍&nbsp;&nbsp;&nbsp;&nbsp; <br/>物&nbsp;&nbsp;&nbsp; 镜：25mm&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <br/>出瞳直径：2.5毫米 <br/>棱镜类型：BK-4&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <br/>聚焦系统：中央聚焦 <br/>视野范围：1000米处为96米&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <br/>棱镜系统：屋脊&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <br/>镜片镀膜：多膜<br/>净&nbsp;&nbsp;&nbsp; 重：331.7克&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <br/>保修时间：一年</p><p>『特性』<br/>&nbsp;&nbsp;&nbsp; 具有防水防雾功能；倍数真实，高清晰度光学素质镜身铝合金，外覆特殊橡胶。 商品坚固耐用，景象清晰逼真。<br/>&nbsp;&nbsp;&nbsp; 此款为经典系列，该系列的特点是：紧凑，镜身内灌入氮气，完全防水，防尘，防雾，橡胶装甲。<br/>&nbsp;&nbsp;&nbsp; tasco经典系列屋脊棱镜双筒望远镜，高质量BAK- 4屋脊棱镜提供防水/ 防雾/防震设计，坚固的橡胶装甲外壳用以防碰撞和擦伤，其具有全天候的可靠性及稳定性。高品质光学多层镀膜的优越透光技术令影像明亮、清晰、纤毫毕露仿佛触手可及。视觉的双重享受让你值得拥有！</p><p>该系列望远镜特点：<br/>&nbsp;&nbsp;&nbsp; 紧凑、镜身内灌入氮气、完全防水、防尘、、防雾、橡胶装甲。&nbsp; <br/>&nbsp;&nbsp;&nbsp; 经典的设计则是永远的时尚！</p><p>『使用推荐』商品坚固耐用，视场清晰，亮度高，像质优良。<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 此款镜身内灌入氮气，具有完全防水、防尘、防雾的特点，最适合于航海、港务、海关、电力、油田、林业、旅游等。</p><p>&nbsp;</p><p>&nbsp;</p>', NULL, '/cms/resources/ueditor/jsp//upload/20150813/41591439427029455.jpg', '美国tasco 10×25望远镜 wp1025', '10113', '美国TASCO望远镜', '2015-08-13 10:05:16', '1', '1');
INSERT INTO `cms`.`t_product` (`id`, `brief`, `content`, `createTime`, `thumbnail`, `title`, `typeId`, `typeName`, `updateTime`, `hot`, `recommend`) VALUES ('100002', 'Jion2028', '<p><img src=\"/cms/resources/ueditor/jsp//upload/20150813/95061439444392886.jpg\"/></p><p>产品编号：2708<br/>1.光学设计:ED复消色差透镜&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <br/>2.目镜:LV20、LV5mm&nbsp;&nbsp; <br/>3.赤道仪:SPHINX SXW式&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <br/>4.焦距:625mm(f/7.7)&nbsp; <br/>5.有效口径:81mm&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <br/>6.分解力:1.43角秒&nbsp;&nbsp; <br/>7.集光力:134x&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <br/>8.极限星等:11.3&nbsp;&nbsp;&nbsp; <br/>9.寻星镜:内红点&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <br/>10.镜筒尺寸:Ø90*L600mm&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <br/>11.镜筒重量:2.3kg&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <br/>12.螺纹摄影口:60、42mm<br/>13.观测口:50.8、31.7mm&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <br/>14.摄影:直焦/放大摄影<br/>15.附属品:STAR BOOK、光路转换镜及配件盒<br/>16.铝制伸缩三脚架:SXW-HAL110型(70-110cm)<br/>17.重锤:1.9kg*1&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <br/>18.总重量:16.2kg</p>', NULL, '/cms/resources/ueditor/jsp/upload/20150813/95061439444392886.jpg', '日本威信VIXEN SXW系列 ED81-SXW 折射式天文望远镜', '10129', '日本威信', '2015-08-13 13:43:18', NULL, '1');
